    for i, (title, points) in enumerate(slides_data, start=2):
        # –î–ª—è –ø–æ—Å–ª–µ–¥–Ω–µ–≥–æ —Å–ª–∞–π–¥–∞ (–ó–∞–∫–ª—é—á–µ–Ω–∏–µ) –∏—Å–ø–æ–ª—å–∑—É–µ–º –º–∞–∫–µ—Ç —Ç–æ–ª—å–∫–æ —Å –∑–∞–≥–æ–ª–æ–≤–∫–æ–º
        if i == 13:
            slide_layout = prs.slide_layouts[5] # –ú–∞–∫–µ—Ç: –¢–æ–ª—å–∫–æ –∑–∞–≥–æ–ª–æ–≤–æ–∫
            slide = prs.slides.add_slide(slide_layout)
            slide.shapes.title.text = title
        else:
            slide = prs.slides.add_slide(content_layout)
            slide.shapes.title.text = f"{i}. {title}"
            body = slide.shapes.placeholders[1]
            tf = body.text_frame
            for point in points:
                p = tf.add_paragraph()
                p.text = point
                p.level = 0
            
    # –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ —Ñ–∞–π–ª–∞
    filename = "Full_Health_Law_and_Marking_Presentation_Final.pptx"
    prs.save(filename)
    print(f"–ü—Ä–µ–∑–µ–Ω—Ç–∞—Ü–∏—è –Ω–∞ 13 —Å–ª–∞–π–¥–æ–≤ —Å–≥–µ–Ω–µ—Ä–∏—Ä–æ–≤–∞–Ω–∞: {filename}")

